async function Bills() {
  const response = await fetch(baseUrl + '/bills');
  const bills = await response.json();
  return (
    <div className='w-full p-10'>
      {bills.map(bill => <SimpleBill key={bill.title} bill={bill} />)}
    </div>
  );
}

